// Check if today is the last day of the month
const today = new Date();
const tomorrow = new Date(today);
tomorrow.setDate(today.getDate() + 1);

// If tomorrow is the 1st, then today is the last day of the month
const isLastDayOfMonth = tomorrow.getDate() === 1;

// Also get the current month info for logging
const currentMonth = today.toLocaleString('default', { month: 'long', year: 'numeric' });
const currentDate = today.getDate();
const currentYear = today.getFullYear();
const currentMonthNum = today.getMonth() + 1;

return [
  {
    json: {
      isLastDayOfMonth: isLastDayOfMonth,
      currentDate: currentDate,
      currentMonth: currentMonth,
      currentMonthNum: currentMonthNum,
      currentYear: currentYear,
      message: isLastDayOfMonth 
        ? `Today is the last day of ${currentMonth}. Proceeding with cleanup.`
        : `Today is day ${currentDate} of ${currentMonth}. Not the last day - skipping cleanup.`,
      executionTime: today.toISOString()
    }
  }
];

// Calculate the cutoff date - first day of previous month
const today = new Date();
let cutoffDate;

// Get first day of previous month
if (today.getMonth() === 0) {
  // If January, go to December of previous year
  cutoffDate = new Date(today.getFullYear() - 1, 11, 1);
} else {
  // Otherwise, go to previous month
  cutoffDate = new Date(today.getFullYear(), today.getMonth() - 1, 1);
}

// Format date for Notion API (ISO 8601)
const cutoffDateISO = cutoffDate.toISOString();

// Also calculate date ranges for reporting
const currentMonth = today.toLocaleString('default', { month: 'long', year: 'numeric' });
const previousMonth = new Date(today.getFullYear(), today.getMonth() - 1, 1)
  .toLocaleString('default', { month: 'long', year: 'numeric' });

return [
  {
    json: {
      cutoffDate: cutoffDateISO,
      cutoffDateFormatted: cutoffDate.toLocaleDateString(),
      currentMonth: currentMonth,
      previousMonth: previousMonth,
      cleanupDescription: `Deleting records older than ${cutoffDate.toLocaleDateString()}`,
      executionTime: new Date().toISOString()
    }
  }
];

// Prepare deletion summary
const records = $input.all();
const cutoffInfo = $node["Calculate Cutoff Date"].json;

let summary = {
  totalRecords: records.length,
  cutoffDate: cutoffInfo.cutoffDateFormatted,
  executionTime: cutoffInfo.executionTime,
  byTeam: {},
  byPlatform: {},
  byMonth: {},
  oldestRecord: null,
  newestRecord: null,
  recordsToDelete: []
};

// Process each record
for (const item of records) {
  const record = item.json;

  // Extract data from flattened properties (n8n returns them with property_ prefix)
  const date = record.property_date?.start || null;
  const projectName = record.property_project_name || 'Unknown';
  const team = record.property_team || 'Unknown';
  const platform = record.property_platform || 'Unknown';
  const previousStatus = record.property_previous_status || 'Unknown';
  const newStatus = record.property_new_status || 'Unknown';
  const version = record.property_version || 'Unknown';

  // Track by team
  if (!summary.byTeam[team]) {
    summary.byTeam[team] = 0;
  }
  summary.byTeam[team]++;

  // Track by platform
  if (!summary.byPlatform[platform]) {
    summary.byPlatform[platform] = 0;
  }
  summary.byPlatform[platform]++;

  // Track by month
  if (date) {
    const monthKey = date.substring(0, 7); // YYYY-MM
    if (!summary.byMonth[monthKey]) {
      summary.byMonth[monthKey] = 0;
    }
    summary.byMonth[monthKey]++;

    // Track oldest and newest
    if (!summary.oldestRecord || date < summary.oldestRecord) {
      summary.oldestRecord = date;
    }
    if (!summary.newestRecord || date > summary.newestRecord) {
      summary.newestRecord = date;
    }
  }

  // Add to deletion list
  summary.recordsToDelete.push({
    id: record.id,
    projectName: projectName,
    date: date,
    statusChange: `${previousStatus} ‚Üí ${newStatus}`,
    team: team,
    platform: platform,
    version: version
  });
}

// Return both summary and records for processing
return [
  {
    json: {
      summary: summary,
      recordsFound: records.length > 0,
      message: records.length > 0 
        ? `Found ${records.length} records to delete` 
        : 'No records found for deletion'
    }
  }
];

// Compile final cleanup report
const summary = $node["Prepare Deletion Summary"].json.summary;
const deletionComplete = true; // Assuming all deletions succeeded

const report = {
  status: 'completed',
  executionTime: summary.executionTime,
  cutoffDate: summary.cutoffDate,
  totalDeleted: summary.totalRecords,
  dateRange: {
    from: summary.oldestRecord,
    to: summary.newestRecord
  },
  breakdown: {
    byTeam: summary.byTeam,
    byPlatform: summary.byPlatform,
    byMonth: summary.byMonth
  },
  message: `Successfully deleted ${summary.totalRecords} records older than ${summary.cutoffDate}`
};

return [{ json: report }];

